INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `comment`) VALUES
(121694, 0, 0, 'Люблю, когда пандарены так отвязно выделываются. Танцуем!', 12, 0, 100, 0, 0, 0, 131046, 'Аштар'),
(121694, 0, 1, 'Давайте, пандарены!', 12, 0, 100, 0, 0, 0, 131047, 'Аштар'),
(121694, 1, 0, 'Давайте, тролли! Покажите всем, как надо танцевать!', 12, 0, 100, 0, 0, 0, 130769, 'Аштар'),
(121694, 1, 1, 'Танец троллей! Эти ноги не удержит никакая обувь!', 12, 0, 100, 0, 0, 0, 130770, 'Аштар'),
(121694, 2, 0, 'Давайте, таурены! Покажите, как танцуют в Мулгоре.', 12, 0, 100, 0, 0, 0, 130772, 'Аштар'),
(121694, 2, 1, 'Смотри по сторонам! А не то тебе таурены-танцоры все ноги отдавят.', 12, 0, 100, 0, 0, 0, 130771, 'Аштар'),
(121694, 3, 0, 'Вперед, братья и сестры орки! Покажите нам свой танец!', 12, 0, 100, 0, 0, 0, 130779, 'Аштар'),
(121694, 3, 0, 'Это наш город, орки. Покажем всем наше величие!', 12, 0, 100, 0, 0, 0, 130780, 'Аштар'),
(121694, 4, 0, 'Покажите нам, как танцуют гоблины!', 12, 0, 100, 0, 0, 0, 130774, 'Аштар'),
(121694, 4, 1, 'Пусть гоблины поднимутся на сцену, чтобы все их видели!', 12, 0, 100, 0, 0, 0, 130773, 'Аштар'),
(121694, 5, 0, 'А теперь выход Отрекшихся. От такого танца того и гляди остановится сердце!', 12, 0, 100, 0, 0, 0, 130768, 'Аштар'),
(121694, 5, 1, 'Приглашаем на сцену Отрекшихся!', 12, 0, 100, 0, 0, 0, 130767, 'Аштар'),
(121694, 6, 0, 'Эльфы крови! Теперь ваш черед.', 12, 0, 100, 0, 0, 0, 130776, 'Аштар'),
(121694, 6, 1, 'Полюбуйтесь на эльфов крови и их изящные движения!', 12, 0, 100, 0, 0, 0, 130775, 'Аштар'),
(121694, 7, 0, 'Выпускайте бычка! Таурены Крутогорья, ВПЕРЕД!', 12, 0, 100, 0, 0, 0, 147264, 'Аштар'),
(121694, 7, 1, 'У пришедших с крутых гор и нрав крутой! Это таурены Крутогорья!', 12, 0, 100, 0, 0, 0, 147263, 'Аштар'),
(121694, 8, 0, 'День воюют, ночь танцуют. Поприветствуйте ночнорожденных!', 12, 0, 100, 0, 0, 0, 147265, 'Аштар'),
(121694, 8, 1, 'Нет, у вас не галлюцинации... просто перед вами танцующие ночнорожденные!', 12, 0, 100, 0, 0, 0, 147266, 'Аштар'),
(121739, 0, 0, 'Люблю, когда пандарены так отвязно выделываются. Танцуем!', 12, 0, 100, 0, 0, 0, 131046, 'Марла'),
(121739, 0, 1, 'Давайте, пандарены!', 12, 0, 100, 0, 0, 0, 131047, 'Марла'),
(121739, 1, 0, 'Это ваш город, люди. Покажите нам все свое величие!', 12, 0, 100, 0, 0, 0, 131034, 'Марла'),
(121739, 1, 1, 'Ходят слухи, что люди не умеют танцевать. Давайте, докажите, что это не так!', 12, 0, 100, 0, 0, 0, 131035, 'Марла'),
(121739, 2, 0, 'Приглашаем на сцену дренеев!', 12, 0, 100, 0, 0, 0, 131042, 'Марла'),
(121739, 2, 1, 'А сейчас нас ждут танцы из других миров. Встречайте: это дренеи!', 12, 0, 100, 0, 0, 0, 131043, 'Марла'),
(121739, 3, 0, 'Пусть гномы поднимутся на сцену, чтобы их все видели!', 12, 0, 100, 0, 0, 0, 131040, 'Марла'),
(121739, 3, 1, 'Гномы! Ну разве они не очаровашки?', 12, 0, 100, 0, 0, 0, 131041, 'Марла'),
(121739, 4, 0, 'Только посмотрите, как ловко дворфы работают ногами! И при этом даже эль умудряются не проливать!', 12, 0, 100, 0, 0, 0, 131036, 'Марла'),
(121739, 4, 1, 'Пора и дворфам показать, как они умеют отрываться!', 12, 0, 100, 0, 0, 0, 131037, 'Марла'),
(121739, 5, 0, 'Полюбуйтесь на изящных и уверенных в себе ночных эльфов! Ой, как же здесь жарко...', 12, 0, 100, 0, 0, 0, 131038, 'Марла'),
(121739, 5, 1, 'Ночные эльфы! Покажите нам, как отрываются в Тельдрассиле!', 12, 0, 100, 0, 0, 0, 131039, 'Марла'),
(121739, 6, 0, 'У-у-у! Давайте, воргены, покажите, какие вы необузданные!', 12, 0, 100, 0, 0, 0, 131045, 'Марла'),
(121739, 6, 1, 'Вперед, воргены, братья и сестры мои! Покажем всем, как нужно танцевать!', 12, 0, 100, 0, 0, 0, 131044, 'Марла'),
