INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `comment`) VALUES
(118354, 0, 0, 'Добро пожаловать на борт. Усаживайтесь поудобнее и наслаждайтесь прогулкой.', 12, 0, 100, 0, 0, 0, 126677, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 1, 0, 'Прямо под вами раскинулся Лес Хрустальной Песни.', 12, 0, 100, 0, 0, 0, 126678, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 2, 0, 'Мне довелось немало постранствовать по свету, но здесь, к сожалению, я провел куда меньше времени, чем мне бы хотелось...', 12, 0, 100, 0, 0, 0, 126679, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 3, 0, 'Нордскол... С этим местом у меня связаны приятные воспоминания.', 12, 0, 100, 0, 0, 0, 126680, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 4, 0, 'Вон за тем хребтом, в низине Шолазар, я повстречал племена оракулов – волчеров и мурлоков. Такие странные создания!', 12, 0, 100, 0, 0, 0, 126681, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 5, 0, 'Из всех моих странствий этот день стоит особняком. Все было как будто вчера...', 12, 0, 100, 0, 0, 0, 126682, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 6, 0, 'Однако я совсем вас заболтал. Наверняка у вас тоже есть интересные истории о своих путешествиях!', 12, 0, 100, 0, 0, 0, 126683, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118354, 7, 0, 'Вот, например, ты, $n – в каких необычных местах тебе довелось побывать? ', 12, 0, 100, 0, 0, 0, 126686, 'Чэнь Буйный Портер to Player'),
(118354, 8, 0, 'А как насчет тебя, $n?', 12, 0, 100, 0, 0, 0, 126687, 'Чэнь Буйный Портер to Player'),
(118354, 9, 0, 'А ты, $n? Что больше всего запомнилось тебе в твоих путешествиях?', 12, 0, 100, 0, 0, 0, 126688, 'Чэнь Буйный Портер to Player'),
(118354, 10, 0, 'Ну ладно, вы пока тут болтайте, а мне нужно править шаром – а то еще врежемся прямо в Даларан, неровен час.', 12, 0, 100, 0, 0, 0, 126689, 'Чэнь Буйный Портер to Воздушный шар Чэня'),
(118470, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Чэнь Буйный Портер to Player'),
(118297, 0, 0, 'Ваша группа приняла решение выбросить Шнотца с воздушного шара!', 42, 0, 100, 0, 0, 0, 127460, 'Воздушный шар Шнотца to Player'),
(118340, 0, 0, 'Поппрошу вашего фнимания.', 12, 0, 100, 0, 0, 0, 126630, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 1, 0, 'Наш шар не очень фместителен.', 12, 0, 100, 0, 0, 0, 126631, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 2, 0, 'У нас строкие прафила: не болтайт, не прыгайт и не раскачивайт гондолу.', 12, 0, 100, 0, 0, 0, 126632, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 3, 0, 'Не скакайт!', 12, 0, 100, 0, 0, 0, 126633, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 4, 0, 'Соблюдайт праффила, иначе вас ждет штрафф.', 12, 0, 100, 0, 0, 0, 126634, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 5, 0, 'Нас ждет удифительный полет. Фсем радофаться. Понимайт?', 12, 0, 100, 0, 0, 0, 126635, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 6, 0, 'Гут. А тепер один час фсем ситеть тихо! ', 12, 0, 100, 0, 0, 0, 126636, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118340, 7, 0, 'Йа это саслужил!', 12, 0, 100, 0, 0, 0, 126638, 'Командующий воздушным шаром Шнотц to Воздушный шар Шнотца'),
(118482, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Командующий воздушным шаром Шнотц to Player'),
(118355, 0, 0, 'Как, опять пассажиры? Ладно, залезайте внутрь...', 12, 0, 100, 0, 0, 0, 126690, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 1, 0, 'Мы направляемся в Полугорье. Прошу руки наружу не высовывать! ', 12, 0, 100, 0, 0, 0, 126691, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 2, 0, '<Вздох>...', 12, 0, 100, 0, 0, 0, 126692, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 3, 0, 'Прямо под нами справа вы видите усадьбу Пана. Обязательно посмотрите...', 12, 0, 100, 0, 0, 0, 126694, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 4, 0, 'А вообще знаете, что? С меня хватит!', 12, 0, 100, 0, 0, 0, 126695, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 5, 0, 'Признаюсь честно вам троим.', 12, 0, 100, 0, 0, 0, 126696, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 6, 0, 'Я ненавижу воздушные шары! Никогда их не любил. Я всегда хотел быть шаманом, но нет же! Ведь "полеты на воздушных шарах у нас в крови"!', 12, 0, 100, 0, 0, 0, 126697, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 7, 0, 'Что, нравится, отец? А? Надеюсь, теперь ты мной доволен!', 12, 0, 100, 0, 0, 0, 126698, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 8, 0, 'Так-то вот...', 12, 0, 100, 0, 0, 0, 126699, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 9, 0, 'Да уж.', 12, 0, 100, 0, 0, 0, 126700, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 10, 0, 'Как камень с души... Никогда еще никому об этом не говорил.', 12, 0, 100, 0, 0, 0, 126701, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 11, 0, 'Теперь вы знаете мою тайну. А у вас есть какой-нибудь секрет?', 12, 0, 100, 0, 0, 0, 126702, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118355, 12, 0, 'Давай, $n. Расскажи нам то, в чем еще никому не $gпризнавался:признавалась;. Начинай!', 12, 0, 100, 0, 0, 0, 126703, 'Линь Облачный Странник to Player'),
(118355, 13, 0, 'А ты, $n? Тебя тяготит какая-нибудь тайна?', 12, 0, 100, 0, 0, 0, 126704, 'Линь Облачный Странник to Player'),
(118355, 14, 0, 'Теперь твой черед, $n. Открой нам какой-нибудь секрет! Мы не осудим! И никому не расскажем!', 12, 0, 100, 0, 0, 0, 126705, 'Линь Облачный Странник to Player'),
(118355, 15, 0, 'Это был самый откровенный полет в моей карьере. Хотя мне даже понравилось. Спасибо вам всем!', 12, 0, 100, 0, 0, 0, 126706, 'Линь Облачный Странник to Экспресс Облачных Странников'),
(118474, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Линь Облачный Странник to Player'),
(118351, 0, 0, 'Прошу на борт, начинающие воздухоплаватели! Кому наклейку с воздушным шаром? Прошу, не стесняйтесь!', 12, 0, 100, 0, 0, 0, 126655, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 1, 0, 'У нас самые шикарные полеты на воздушном шаре во всем Азероте! Кого угодно спросите – вам подтвердят!', 12, 0, 100, 0, 0, 0, 126656, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 2, 0, 'Сегодня мы полетим над Азсуной, жемчужиной Расколотых островов.', 12, 0, 100, 0, 0, 0, 126658, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 3, 0, 'Прямо под нами находится академия Нар''таласа. Это, вроде бы, древняя школа магов.', 12, 0, 100, 0, 0, 0, 126660, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 4, 0, 'Рангл, разожги-ка мангал да подготовь фейерверки.', 12, 0, 100, 0, 0, 0, 126662, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 5, 0, 'Вы, наверное, удивляетесь: как я умудряюсь организовывать столь роскошные экскурсии на воздушном шаре и до сих пор не вылетел в трубу?', 12, 0, 100, 0, 0, 0, 126664, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 6, 0, 'Все очень просто – дело в том, что о стоимости тура клиенты узнают, когда шар уже находится в воздухе!', 12, 0, 100, 0, 0, 0, 126665, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 7, 0, 'Так что с вас с каждого по двадцать восемь косарей золотых – плюс чаевые. Давайте раскошеливайтесь, если не хотите, чтобы Рангл переломал вам ноги.', 12, 0, 100, 0, 0, 0, 126666, 'Боско to Небесная колесница Боско и Рангла'),
(118351, 8, 0, 'ЧТО?! Рангл, ты опять накосячил!!!', 12, 0, 100, 0, 0, 0, 126668, 'Боско to Небесная колесница Боско и Рангла'),
(118352, 0, 0, 'Не угодно ли бесплатных напитков? Угощайтесь!', 12, 0, 100, 0, 0, 0, 126657, 'Рангл to Боско'),
(118352, 1, 0, 'Икры кто-нибудь желает? Она у нас отменная.', 12, 0, 100, 0, 0, 0, 126659, 'Рангл to Боско'),
(118352, 2, 0, 'Горячее полотенце? Все для наших клиентов!', 12, 0, 100, 0, 0, 0, 126661, 'Рангл to Боско'),
(118352, 3, 0, 'Не вопрос, босс.', 12, 0, 100, 0, 0, 0, 126663, 'Рангл to Боско'),
(118352, 4, 0, 'Готово, босс. Я разложил фейерверки на мангале, как ты и велел.', 12, 0, 100, 0, 0, 0, 126667, 'Рангл to Боско'),
(118467, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Боско to Player'),
(118468, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Рангл to Player'),
(118357, 0, 0, 'Приветствую! Заходите в гондолу... и готовьтесь к самому поучительному путешествию в своей жизни!', 12, 0, 100, 0, 0, 0, 126719, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 1, 0, 'Сегодня нам предстоит совершить обзорный полет над древним городом Сурамаром.', 12, 0, 100, 0, 0, 0, 126720, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 2, 0, 'Но сначала проведем небольшую перекличку.', 12, 0, 100, 0, 0, 0, 126721, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 3, 0, '$n? $n здесь?', 12, 0, 100, 0, 0, 0, 126722, 'Цзан Облачный Странник to Player'),
(118357, 4, 0, 'Просто ответь "здесь".', 12, 0, 100, 0, 0, 0, 126723, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 5, 0, 'Хорошо. А $n здесь?', 12, 0, 100, 0, 0, 0, 126724, 'Цзан Облачный Странник to Player'),
(118357, 6, 0, 'Отметил. Далее по списку – $n. Это, я так понимаю, ты?', 12, 0, 100, 0, 0, 0, 126725, 'Цзан Облачный Странник to Player'),
(118357, 7, 0, 'Ну все, хватит разговоров. Отправляемся!', 12, 0, 100, 0, 0, 0, 126726, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 8, 0, 'Как вы знаете, Сурамар – дом ночнорожденных эльфов.', 12, 0, 100, 0, 0, 0, 126727, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 9, 0, 'Помимо прочего, ночнорожденные славятся своим сварливым характером.', 12, 0, 100, 0, 0, 0, 126729, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 10, 0, '$n, скажи мне, какую необычную субстанцию используют ночнорожденные для релаксации?', 12, 0, 100, 0, 0, 0, 126730, 'Цзан Облачный Странник to Player'),
(118357, 11, 0, 'Правильный ответ – конечно же, ману.', 12, 0, 100, 0, 0, 0, 126731, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 12, 0, 'Из-за огромного количества маны, что они потребляют, их тела претерпели изменения.', 12, 0, 100, 0, 0, 0, 126732, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 13, 0, '$n, какая часть тела ночнорожденных претерпела наиболее заметные изменения из-за чрезмерного потребления маны?', 12, 0, 100, 0, 0, 0, 126733, 'Цзан Облачный Странник to Player'),
(118357, 14, 0, 'Почти угадали! Это их кожа.', 12, 0, 100, 0, 0, 0, 126734, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 15, 0, 'И, наконец, $n, последний вопрос. Как звучит традиционное приветствие в Сурамаре?', 12, 0, 100, 0, 0, 0, 126735, 'Цзан Облачный Странник to Player'),
(118357, 16, 0, 'Неплохая версия! Правильный ответ: "Это иллюзия! Что ты скрываешь?"', 12, 0, 100, 0, 0, 0, 126736, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118357, 17, 0, 'На сегодня урок окончен. Благодарю вас за полет... и подготовьтесь в следующий раз получше!', 12, 0, 100, 0, 0, 0, 126737, 'Цзан Облачный Странник to Экспресс Облачных Странников'),
(118436, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Цзан Облачный Странник to Player'),
(94409, 0, 0, 'Думаю, ты отлично впишешься в мой охотничий отряд!', 12, 0, 100, 0, 0, 0, 121032, 'Хеминг Эрнестуэй to Player'),
(94409, 1, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Хеминг Эрнестуэй to Player'),
(118353, 0, 0, 'Друзья, рад приветствовать вас на воздушном шаре Эрнестуэя! Вас ждет увлекательная экскурсия по Крутогорью.', 12, 0, 100, 0, 0, 0, 126669, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 1, 0, 'Я этот шар купил у гоблина, который утверждал, что с него очень удобно стрелять по лосям!', 12, 0, 100, 0, 0, 0, 126670, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 2, 0, 'Увы, в последнее время... В общем, не знаю. Как-то все уныло. Охота уже не приносит мне былой радости.', 12, 0, 100, 0, 0, 0, 126671, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 3, 0, 'Даже пальба по зверью с воздушного шара уже не горячит кровь так, как прежде.', 12, 0, 100, 0, 0, 0, 126672, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 4, 0, 'Ну вот, опять меня одолела меланхолия.', 12, 0, 100, 0, 0, 0, 127343, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 5, 0, 'Кто-нибудь знает хороший анекдот? Ну вот например...', 12, 0, 100, 0, 0, 0, 127344, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 6, 0, 'Почему дворфы-охотники предпочитают ружья?', 12, 0, 100, 0, 0, 0, 127345, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 7, 0, 'Потому что в лук нельзя налить эль!', 12, 0, 100, 0, 0, 0, 127346, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 8, 0, 'Хе-хе! И не смотрите на меня, мне так шутить можно. Я ведь сам и дворф – и охотник!', 12, 0, 100, 0, 0, 0, 127347, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 9, 0, '$n, теперь твоя очередь.', 12, 0, 100, 0, 0, 0, 126673, 'Хеминг Эрнестуэй to Player'),
(118353, 10, 0, 'А как насчет тебя, $n?', 12, 0, 100, 0, 0, 0, 126674, 'Хеминг Эрнестуэй to Player'),
(118353, 11, 0, 'Ну или ты, $n. Кто-нибудь, развеселите меня!', 12, 0, 100, 0, 0, 0, 126675, 'Хеминг Эрнестуэй to Player'),
(118353, 12, 0, 'Ой-ой... близковато мы подошли... Все помашите ручкой Громовому Тотему!', 12, 0, 100, 0, 0, 0, 126676, 'Хеминг Эрнестуэй to Привет, оружие'),
(118353, 13, 0, 'Ну что, я снова повеселел! Спасибо, что составили компанию старому охотнику!', 12, 0, 100, 0, 0, 0, 127348, 'Хеминг Эрнестуэй to Привет, оружие'),
(118356, 0, 0, 'Приветствую вас на борту! Сегодня я буду вашим гидом по Штормхейму.', 12, 0, 100, 0, 0, 0, 126707, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 1, 0, 'Итак, видите там внизу что-то виднеется? Это... хм... это Штормхейм.', 12, 0, 100, 0, 0, 0, 126708, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 2, 0, 'Простите, я не самый опытный гид на свете.', 12, 0, 100, 0, 0, 0, 126709, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 3, 0, 'Может... во что-нибудь поиграем в полете?', 12, 0, 100, 0, 0, 0, 126710, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 4, 0, 'Я знаю одну игру. Она называется "Поцелуй, женись или убей."', 12, 0, 100, 0, 0, 0, 126711, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 5, 0, 'Правила простые: вначале выбираешь кого-то, кого бы ты $gпоцеловал:поцеловала;, затем кого-то, $gна ком бы ты женился:за кого бы ты вышла замуж;, а потом кого-то, кого бы ты $gубил:убила;.', 12, 0, 100, 0, 0, 0, 126712, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 6, 0, 'Итак... Мой первый выбор...', 12, 0, 100, 0, 0, 0, 126713, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118356, 7, 0, '$n, у тебя милое лицо, плюс ты $gединственный:единственная;, кто тут чистит зубы. Так что я бы поцеловала тебя.', 12, 0, 100, 0, 0, 0, 126714, 'Эми-Лань to Player'),
(118356, 8, 0, 'И, конечно же, я бы убила |3-3($n).', 12, 0, 100, 0, 0, 0, 126715, 'Эми-Лань to Player'),
(118356, 9, 0, 'Остается $n. Выходит за тебя бы я вышла замуж. Не самый плохой вариант.', 12, 0, 100, 0, 0, 0, 126716, 'Эми-Лань to Player'),
(118356, 10, 0, 'Все, теперь ваша очередь!', 12, 0, 100, 0, 0, 0, 126717, 'Эми-Лань to Воздушный лайнер Эми-Лань'),
(118443, 0, 0, 'Воздушный шар отправится в полет, как только три пассажира будут готовы.', 12, 0, 100, 0, 0, 0, 127453, 'Эми-Лань to Player');
